// init wow js
new WOW().init();


$(document).ready(function() {

    $(window).scroll(function() {
        // Show service slider black content
        if (($(this).scrollTop()) > ($('.homepage .section-service-slider ').position().top - 200)) {
            $('.homepage .title-slider-item').attr('style', 'opacity: 1;')
        }
    });

    // typed.js Main heading
    var typed = new Typed('#typed-main-heading', {
        stringsElement: '#main-heading',
        typeSpeed: 50,
        showCursor: true
    });

    setTimeout(() => {
        $('.typed-cursor').text('.')
    }, 3100);


    // var owl = $('.owl-carousel');
    var heroSlider = $('.hero-slider.owl-carousel');
    var serviceSlider = $('.service-slider.owl-carousel');
    var titleSlider = $('.title-slider.owl-carousel');
    var reviewSlider = $('.review-slider.owl-carousel');

    heroSlider.owlCarousel({
        dots: false,
        nav: true,
        navElement: 'div',
        items: 1,
        loop: true,
        margin: 0,
        autoplay: true,
        autoplayTimeout: 5000,
        smartSpeed: 800,
        autoplayHoverPause: true
    });
    serviceSlider.owlCarousel({
        dots: false,
        nav: true,
        navElement: 'div',
        items: 1,
        loop: true,
        margin: 0,
        autoplay: false,
        autoplayTimeout: 5000,
        smartSpeed: 800,
        autoplayHoverPause: true,
        mouseDrag: false,
        touchDrag: false,
        onInitialized: counter,
        onChanged: counter,
    });
    titleSlider.owlCarousel({
        dots: false,
        nav: true,
        navElement: 'div',
        items: 1,
        loop: true,
        margin: 0,
        autoplay: false,
        autoplayTimeout: 5000,
        smartSpeed: 800,
        animateOut: 'fadeOut',
        autoplayHoverPause: true,
        mouseDrag: false,
        touchDrag: false
    });
    reviewSlider.owlCarousel({
        dots: false,
        nav: true,
        navElement: 'div',
        items: 1,
        loop: true,
        margin: 0,
        autoplay: true,
        autoplayTimeout: 5000,
        smartSpeed: 800,
        autoplayHoverPause: true,
        mouseDrag: false,
        touchDrag: false
    });


    // Set service slider current index and items length
    function counter(event) {
        if (!event.namespace) {
            return;
        }
        var slides = event.relatedTarget;
        $('.current-item').text(slides.relative(slides.current()) + 1 + '/' + slides.items().length);
    }

    // Trig service slider image by clicking arrow
    $('.title-slider .owl-next').click(function() {
        serviceSlider.trigger('next.owl.carousel');
    })
    $('.title-slider .owl-prev').click(function() {
        serviceSlider.trigger('prev.owl.carousel');
    })

});