$(document).ready(function() {

    $(window).scroll(function() {
        if ($(this).scrollTop() > 10) {
            $('.header-container').addClass('with-bg')
        } else {
            $('.header-container').removeClass('with-bg')
        }
    });

    function openMenu() {
        $('.menu-container').addClass('open')
        $('.menu-li').addClass('show')
        $('.menu-brand-logo').addClass('show')
        $('body').addClass('scroll-off')
        if ($(window).width() < 768) {
            $('.menu-socials a').addClass('show')
        }
    }

    function closeMenu() {
        $('.menu-container').removeClass('open')
        $('.menu-li').removeClass('show')
        $('.menu-brand-logo').removeClass('show')
        $('body').removeClass('scroll-off')
        if ($(window).width() < 768) {
            $('.menu-socials a').removeClass('show')
        }
    }

    // Contact click goes to contact section
    $('.contact-nav-link').click(function() {
        closeMenu()
        var position = $(".contact").offset().top;
        $('body,html').animate({
            scrollTop: position * 2
        }, 2000);
        return false;
    });

    // Open menu
    $(".hamburger-btn").click(function() {
        openMenu()
    })

    // Close menu
    $(".close-btn").click(function() {
        closeMenu()
    })
})