// init lazy loading
const observer = lozad();
observer.observe();

// // init wow js
new WOW().init();

$(document).ready(function() {
    $('.hero-wave').fadeIn(2000)

    $(window).scroll(function() {
        $('.project-type').each(function() {
            if ($(window).scrollTop() > ($(this).position().top - 500)) {
                $(this).addClass('animate-wave')
            }
        })
    })
})