// init wow js
new WOW().init();


$(document).ready(function() {

    // Remove images overlay onScroll
    $(window).scroll(function() {
        $('.figure-wrapper').each(function() {
            if ($(window).scrollTop() > $(this).position().top) {
                $(this).find('.overlay').addClass('remove-overlay')
            }
        })
    })

    // Show hero title
    $('.hero-title').addClass('show')

    var projectSlider = $('.current-project-slider.owl-carousel');
    var sliderChildrenCount = projectSlider.children().length;



    projectSlider.owlCarousel({
        dots: false,
        nav: true,
        navElement: 'div',
        items: 1,
        loop: true,
        margin: 0,
        autoplay: true,
        autoplayTimeout: 5000,
        smartSpeed: 800,
        autoplayHoverPause: true,
        mouseDrag: false,
        touchDrag: false
    });

    if (sliderChildrenCount === 1) {
        $('.current-project-slider .owl-nav').addClass('hide');
    }

});