// init lazy loading
const observer = lozad();
observer.observe();

// // init wow js
new WOW().init();

$(document).ready(function() {
    $('.hero-wave').fadeIn(2000)
    $('.hero-title').addClass('animate')
    $('.hero-paragraph').addClass('animate')

    $('.service-btn').click(function() {
        var serviceBtn = $(this)
        if (!$(this).hasClass('active')) {
            $('.service-btn').removeClass('active')
            $(this).addClass('active')
        }

        $(".current-service").each(function() {
            if (serviceBtn.attr('data-service') === $(this).attr('data-service')) {
                $(this).addClass('active')
            } else {
                $(this).removeClass('active')
            }
        });
    })

    // Remove images overlay onScroll
    $(window).scroll(function() {
        if ($(window).scrollTop() > ($('.current-service').position().top) - 200) {
            $('.section-services').find('.overlay').addClass('remove-overlay')
        }
        if ($(window).scrollTop() > ($('.current-service-footer').position().top) - 400) {
            $('.footer-figure').find('.overlay').addClass('remove-overlay')
        }
    })

})